import React, { useState, useEffect } from 'react';
import './Jobs.css';

interface JobPosting {
  id: number;
  title: string;
  description: string;
  company: string;
  location: string;
  jobType: string;
  experienceLevel: string;
  requirements?: string;
  responsibilities?: string;
  benefits?: string;
  salaryMin?: number;
  salaryMax?: number;
  salaryCurrency: string;
  applicationUrl?: string;
  contactEmail?: string;
  status: string;
  applicationDeadline?: string;
  postedByName: string;
  postedByEmail: string;
  createdAt: string;
  updatedAt: string;
  applicationCount: number;
}

interface JobApplication {
  id: number;
  applicant: {
    id: number;
    firstName: string;
    lastName: string;
    email: string;
  };
  jobPosting: JobPosting;
  coverLetter?: string;
  resumeUrl?: string;
  status: string;
  notes?: string;
  createdAt: string;
  updatedAt: string;
}

const Jobs: React.FC = () => {
  const [jobs, setJobs] = useState<JobPosting[]>([]);
  const [myApplications, setMyApplications] = useState<JobApplication[]>([]);
  const [myJobPostings, setMyJobPostings] = useState<JobPosting[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('browse');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedJobType, setSelectedJobType] = useState('');
  const [selectedExperienceLevel, setSelectedExperienceLevel] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showApplicationForm, setShowApplicationForm] = useState(false);
  const [selectedJob, setSelectedJob] = useState<JobPosting | null>(null);
  const [jobTypes, setJobTypes] = useState<string[]>([]);
  const [experienceLevels, setExperienceLevels] = useState<string[]>([]);
  const [userEmail, setUserEmail] = useState('');

  // Form states
  const [jobForm, setJobForm] = useState({
    title: '',
    description: '',
    company: '',
    location: '',
    jobType: 'FULL_TIME' as const,
    experienceLevel: 'ENTRY_LEVEL' as const
  });

  const [applicationForm, setApplicationForm] = useState({
    coverLetter: '',
    resumeUrl: ''
  });

  useEffect(() => {
    const email = localStorage.getItem('userEmail');
    console.log('Retrieved user email from localStorage:', email);
    if (email) {
      setUserEmail(email);
    } else {
      console.error('No user email found in localStorage');
    }
    loadJobs();
    loadEnums();
  }, []);

  useEffect(() => {
    if (activeTab === 'my-applications') {
      loadMyApplications();
    } else if (activeTab === 'my-postings') {
      loadMyJobPostings();
    }
  }, [activeTab, userEmail]);

  const loadJobs = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/jobs');
      if (response.ok) {
        const data = await response.json();
        setJobs(data);
      } else {
        setError('Failed to load jobs');
      }
    } catch (err) {
      setError('Failed to load jobs');
    } finally {
      setLoading(false);
    }
  };

  const loadEnums = async () => {
    try {
      const [jobTypesRes, experienceLevelsRes] = await Promise.all([
        fetch('/api/jobs/types'),
        fetch('/api/jobs/experience-levels')
      ]);
      
      if (jobTypesRes.ok && experienceLevelsRes.ok) {
        const jobTypesData = await jobTypesRes.json();
        const experienceLevelsData = await experienceLevelsRes.json();
        setJobTypes(jobTypesData);
        setExperienceLevels(experienceLevelsData);
      }
    } catch (err) {
      console.error('Failed to load enums:', err);
    }
  };

  const loadMyApplications = async () => {
    if (!userEmail) return;
    
    try {
      const response = await fetch(`/api/jobs/applications/my?userEmail=${userEmail}`);
      if (response.ok) {
        const data = await response.json();
        setMyApplications(data);
      }
    } catch (err) {
      console.error('Failed to load applications:', err);
    }
  };

  const loadMyJobPostings = async () => {
    if (!userEmail) return;
    
    try {
      const response = await fetch(`/api/jobs/my-postings?userEmail=${userEmail}`);
      if (response.ok) {
        const data = await response.json();
        setMyJobPostings(data);
      }
    } catch (err) {
      console.error('Failed to load job postings:', err);
    }
  };

  const searchJobs = async () => {
    if (!searchTerm.trim()) {
      loadJobs();
      return;
    }
    
    try {
      const response = await fetch(`/api/jobs/search?q=${encodeURIComponent(searchTerm)}`);
      if (response.ok) {
        const data = await response.json();
        setJobs(data);
      }
    } catch (err) {
      setError('Failed to search jobs');
    }
  };

  const filterJobs = () => {
    let filteredJobs = jobs;
    
    if (selectedJobType) {
      filteredJobs = filteredJobs.filter(job => job.jobType === selectedJobType);
    }
    
    if (selectedExperienceLevel) {
      filteredJobs = filteredJobs.filter(job => job.experienceLevel === selectedExperienceLevel);
    }
    
    setJobs(filteredJobs);
  };

  const createJobPosting = async (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Form submission triggered!');
    
    if (!userEmail) {
      console.error('No user email found');
      setError('User email not found. Please log in again.');
      return;
    }
    
    console.log('Form data:', jobForm);
    console.log('User email:', userEmail);
    
    try {
      const requestBody = JSON.stringify(jobForm);
      console.log('Request body:', requestBody);
      
      const response = await fetch(`/api/jobs?userEmail=${encodeURIComponent(userEmail)}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: requestBody,
      });
      
      console.log('Response status:', response.status);
      console.log('Response headers:', response.headers);
      
      if (response.ok) {
        const createdJob = await response.json();
        console.log('Job created successfully:', createdJob);
        
        setShowCreateForm(false);
        setJobForm({
          title: '', 
          description: '', 
          company: '', 
          location: '', 
          jobType: 'FULL_TIME', 
          experienceLevel: 'ENTRY_LEVEL'
        });
        setError(''); // Clear any previous errors
        loadJobs();
        alert('Job posted successfully!');
      } else {
        const errorText = await response.text();
        console.error('Failed to create job posting:', errorText);
        setError(`Failed to create job posting: ${errorText}`);
      }
    } catch (err) {
      console.error('Error creating job posting:', err);
      setError('Failed to create job posting. Please try again.');
    }
  };

  const applyForJob = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!selectedJob) return;
    
    try {
      const response = await fetch(`/api/jobs/${selectedJob.id}/apply?userEmail=${userEmail}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(applicationForm),
      });
      
      if (response.ok) {
        setShowApplicationForm(false);
        setSelectedJob(null);
        setApplicationForm({ coverLetter: '', resumeUrl: '' });
        loadMyApplications();
        alert('Application submitted successfully!');
      } else {
        setError('Failed to submit application');
      }
    } catch (err) {
      setError('Failed to submit application');
    }
  };

  const deleteJobPosting = async (jobId: number) => {
    if (!window.confirm('Are you sure you want to delete this job posting?')) return;
    
    try {
      const response = await fetch(`/api/jobs/${jobId}?userEmail=${encodeURIComponent(userEmail)}`, {
        method: 'DELETE',
      });
      
      if (response.ok) {
        loadJobs();
        loadMyJobPostings();
        alert('Job deleted successfully!');
      } else {
        setError('Failed to delete job posting');
      }
    } catch (err) {
      setError('Failed to delete job posting');
    }
  };

  const formatSalary = (min?: number, max?: number, currency: string = 'USD') => {
    if (!min && !max) return 'Not specified';
    if (min && max) return `${currency} ${min.toLocaleString()} - ${max.toLocaleString()}`;
    if (min) return `${currency} ${min.toLocaleString()}+`;
    if (max) return `Up to ${currency} ${max.toLocaleString()}`;
    return 'Not specified';
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'ACTIVE': return 'green';
      case 'CLOSED': return 'red';
      case 'EXPIRED': return 'orange';
      default: return 'gray';
    }
  };

  const getApplicationStatusColor = (status: string) => {
    switch (status) {
      case 'PENDING': return 'orange';
      case 'REVIEWING': return 'blue';
      case 'INTERVIEW_SCHEDULED': return 'purple';
      case 'INTERVIEWED': return 'indigo';
      case 'OFFER_EXTENDED': return 'green';
      case 'ACCEPTED': return 'green';
      case 'REJECTED': return 'red';
      case 'WITHDRAWN': return 'gray';
      default: return 'gray';
    }
  };

  if (loading) {
    return <div className="jobs-container">Loading jobs...</div>;
  }

  return (
    <div className="jobs-container">
      <div className="jobs-header">
        <h2>Job Board</h2>
        <div className="jobs-tabs">
          <button 
            className={activeTab === 'browse' ? 'active' : ''} 
            onClick={() => setActiveTab('browse')}
          >
            Browse Jobs
          </button>
          <button 
            className={activeTab === 'my-applications' ? 'active' : ''} 
            onClick={() => setActiveTab('my-applications')}
          >
            My Applications
          </button>
          <button 
            className={activeTab === 'my-postings' ? 'active' : ''} 
            onClick={() => setActiveTab('my-postings')}
          >
            My Job Postings
          </button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      {activeTab === 'browse' && (
        <div className="jobs-browse">
          <div className="jobs-search-filter">
            <div className="search-section">
              <input
                type="text"
                placeholder="Search jobs..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && searchJobs()}
              />
              <button onClick={searchJobs}>Search</button>
            </div>
            
            <div className="filter-section">
              <select 
                value={selectedJobType} 
                onChange={(e) => setSelectedJobType(e.target.value)}
              >
                <option value="">All Job Types</option>
                {jobTypes.map(type => (
                  <option key={type} value={type}>{type.replace('_', ' ')}</option>
                ))}
              </select>
              
              <select 
                value={selectedExperienceLevel} 
                onChange={(e) => setSelectedExperienceLevel(e.target.value)}
              >
                <option value="">All Experience Levels</option>
                {experienceLevels.map(level => (
                  <option key={level} value={level}>{level.replace('_', ' ')}</option>
                ))}
              </select>
              
              <button onClick={filterJobs}>Filter</button>
              <button onClick={() => {
                setSelectedJobType('');
                setSelectedExperienceLevel('');
                loadJobs();
              }}>Clear</button>
            </div>
            
            <button 
              className="create-job-btn"
              onClick={() => setShowCreateForm(true)}
            >
              Post a Job
            </button>
          </div>

          <div className="jobs-list">
            {jobs.length === 0 ? (
              <div className="no-jobs">No jobs found</div>
            ) : (
              jobs.map(job => (
                <div key={job.id} className="job-card">
                  <div className="job-header">
                    <h3>{job.title}</h3>
                    <span className={`status-badge ${getStatusColor(job.status)}`}>
                      {job.status}
                    </span>
                  </div>
                  
                  <div className="job-company">{job.company}</div>
                  <div className="job-location">{job.location}</div>
                  
                  <div className="job-details">
                    <span className="job-type">{job.jobType.replace('_', ' ')}</span>
                    <span className="experience-level">{job.experienceLevel.replace('_', ' ')}</span>
                    <span className="salary">{formatSalary(job.salaryMin, job.salaryMax, job.salaryCurrency)}</span>
                  </div>
                  
                  <div className="job-description">
                    {job.description.length > 200 
                      ? `${job.description.substring(0, 200)}...` 
                      : job.description
                    }
                  </div>
                  
                  <div className="job-footer">
                    <div className="job-meta">
                      <span>Posted by: {job.postedByName}</span>
                      <span>Applications: {job.applicationCount}</span>
                      <span>Posted: {formatDate(job.createdAt)}</span>
                    </div>
                    
                    <div className="job-actions">
                      <button onClick={() => {
                        setSelectedJob(job);
                        setShowApplicationForm(true);
                      }}>
                        Apply Now
                      </button>
                      {job.applicationUrl && (
                        <a href={job.applicationUrl} target="_blank" rel="noopener noreferrer">
                          External Apply
                        </a>
                      )}
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      )}

      {activeTab === 'my-applications' && (
        <div className="my-applications">
          <h3>My Job Applications</h3>
          {myApplications.length === 0 ? (
            <div className="no-applications">No applications yet</div>
          ) : (
            <div className="applications-list">
              {myApplications.map(application => (
                <div key={application.id} className="application-card">
                  <div className="application-header">
                    <h4>{application.jobPosting.title}</h4>
                    <span className={`status-badge ${getApplicationStatusColor(application.status)}`}>
                      {application.status.replace('_', ' ')}
                    </span>
                  </div>
                  
                  <div className="application-company">{application.jobPosting.company}</div>
                  <div className="application-date">Applied: {formatDate(application.createdAt)}</div>
                  
                  {application.coverLetter && (
                    <div className="application-cover-letter">
                      <strong>Cover Letter:</strong> {application.coverLetter}
                    </div>
                  )}
                  
                  {application.resumeUrl && (
                    <div className="application-resume">
                      <a href={application.resumeUrl} target="_blank" rel="noopener noreferrer">
                        View Resume
                      </a>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {activeTab === 'my-postings' && (
        <div className="my-postings">
          <div className="my-postings-header">
            <h3>My Job Postings</h3>
            <button onClick={() => setShowCreateForm(true)}>Post New Job</button>
          </div>
          
          {myJobPostings.length === 0 ? (
            <div className="no-postings">No job postings yet</div>
          ) : (
            <div className="postings-list">
              {myJobPostings.map(job => (
                <div key={job.id} className="posting-card">
                  <div className="posting-header">
                    <h4>{job.title}</h4>
                    <span className={`status-badge ${getStatusColor(job.status)}`}>
                      {job.status}
                    </span>
                  </div>
                  
                  <div className="posting-company">{job.company}</div>
                  <div className="posting-location">{job.location}</div>
                  
                  <div className="posting-details">
                    <span>{job.jobType.replace('_', ' ')}</span>
                    <span>{job.experienceLevel.replace('_', ' ')}</span>
                    <span>{formatSalary(job.salaryMin, job.salaryMax, job.salaryCurrency)}</span>
                  </div>
                  
                  <div className="posting-meta">
                    <span>Applications: {job.applicationCount}</span>
                    <span>Posted: {formatDate(job.createdAt)}</span>
                  </div>
                  
                  <div className="posting-actions">
                    <button onClick={() => {
                      setSelectedJob(job);
                      // Load applications for this job
                    }}>
                      View Applications
                    </button>
                    <button onClick={() => deleteJobPosting(job.id)}>
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Create Job Form Modal */}
      {showCreateForm && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Post a New Job</h3>
              <button onClick={() => setShowCreateForm(false)}>&times;</button>
            </div>
            
            <form onSubmit={createJobPosting} className="job-form">
              <input
                type="text"
                placeholder="Job Title *"
                value={jobForm.title}
                onChange={(e) => setJobForm({...jobForm, title: e.target.value})}
                required
              />
              
              <input
                type="text"
                placeholder="Company *"
                value={jobForm.company}
                onChange={(e) => setJobForm({...jobForm, company: e.target.value})}
                required
              />
              
              <input
                type="text"
                placeholder="Location"
                value={jobForm.location}
                onChange={(e) => setJobForm({...jobForm, location: e.target.value})}
              />
              
              <select
                value={jobForm.jobType}
                onChange={(e) => setJobForm({...jobForm, jobType: e.target.value as any})}
                required
              >
                <option value="FULL_TIME">Full Time</option>
                <option value="PART_TIME">Part Time</option>
                <option value="CONTRACT">Contract</option>
                <option value="INTERNSHIP">Internship</option>
                <option value="REMOTE">Remote</option>
              </select>
              
              <select
                value={jobForm.experienceLevel}
                onChange={(e) => setJobForm({...jobForm, experienceLevel: e.target.value as any})}
                required
              >
                <option value="ENTRY_LEVEL">Entry Level</option>
                <option value="JUNIOR">Junior</option>
                <option value="MID_LEVEL">Mid Level</option>
                <option value="SENIOR">Senior</option>
                <option value="LEAD">Lead</option>
              </select>
              
              <textarea
                placeholder="Job Description *"
                value={jobForm.description}
                onChange={(e) => setJobForm({...jobForm, description: e.target.value})}
                required
              />
              
              <div className="form-actions">
                <button type="submit">Post Job</button>
                <button type="button" onClick={() => setShowCreateForm(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Application Form Modal */}
      {showApplicationForm && selectedJob && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Apply for {selectedJob.title}</h3>
              <button onClick={() => setShowApplicationForm(false)}>&times;</button>
            </div>
            
            <form onSubmit={applyForJob} className="application-form">
              <div className="job-summary">
                <h4>{selectedJob.title}</h4>
                <p>{selectedJob.company}  {selectedJob.location}</p>
                <p>{selectedJob.jobType.replace('_', ' ')}  {selectedJob.experienceLevel.replace('_', ' ')}</p>
              </div>
              
              <textarea
                placeholder="Cover Letter (optional)"
                value={applicationForm.coverLetter}
                onChange={(e) => setApplicationForm({...applicationForm, coverLetter: e.target.value})}
              />
              
              <input
                type="url"
                placeholder="Resume URL (optional)"
                value={applicationForm.resumeUrl}
                onChange={(e) => setApplicationForm({...applicationForm, resumeUrl: e.target.value})}
              />
              
              <div className="form-actions">
                <button type="submit">Submit Application</button>
                <button type="button" onClick={() => setShowApplicationForm(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Jobs;
